Property.Value:
BEGIN

	SET NOCOUNT ON;
	SET TRAN ISOLATION LEVEL READ UNCOMMITTED;
	SET DEADLOCK_PRIORITY LOW;
	SET XACT_ABORT ON;
	BEGIN TRANSACTION;
		INSERT INTO [ServerAnalysis].[Analysis].[QueryElapsedTime]
		([ServerName],[DatabaseName],[DateKey],[TimeKey],[Object_Name],[Total_Seconds],[Execution_Count],[Query])
		SELECT TOP 100
			@@SERVERNAME,
			DB_NAME(qt.dbid),
			CONVERT(VARCHAR(10), GETDATE(),112) [DateKey],
			LEFT((REPLACE(CONVERT(VARCHAR(10), GETDATE(),108),':','')),3)+'000' [TimeKey],
			o.name AS [object_name],
			qs.total_elapsed_time / 1000000.0 AS total_seconds,
			qs.execution_count,
			SUBSTRING (qt.text,qs.statement_start_offset/2, 
						(CASE	WHEN qs.statement_end_offset = -1 THEN LEN(CONVERT(NVARCHAR(MAX), qt.text)) * 2 
								ELSE qs.statement_end_offset END - qs.statement_start_offset)/2) AS individual_query
		FROM	sys.dm_exec_query_stats qs
				CROSS APPLY sys.dm_exec_sql_text(qs.sql_handle) as qt
				LEFT OUTER JOIN sys.objects o ON qt.objectid = o.object_id
		WHERE	qt.dbid = DB_ID()
			AND qs.last_execution_time > DATEADD(day, -1,GETDATE())
		ORDER BY (qs.total_elapsed_time / qs.execution_count / 1000000.0) DESC;
	COMMIT;
	SET TRAN ISOLATION LEVEL READ COMMITTED;
	RETURN 0;

END

CREATE PROCEDURE [dbo].[CommandExecute]

@Command nvarchar(max),
@CommandType nvarchar(max),
@Mode int,
@Comment nvarchar(max) = NULL,
@DatabaseName nvarchar(max) = NULL,
@SchemaName nvarchar(max) = NULL,
@ObjectName nvarchar(max) = NULL,
@ObjectType nvarchar(max) = NULL,
@IndexName nvarchar(max) = NULL,
@IndexType int = NULL,
@StatisticsName nvarchar(max) = NULL,
@PartitionNumber int = NULL,
@ExtendedInfo xml = NULL,
@LogToTable nvarchar(max),
@Execute nvarchar(max)

AS

BEGIN
SET NOCOUNT ON;
SET TRAN ISOLATION LEVEL READ UNCOMMITTED;
SELECT @StartDate = (CASE WHEN @StartDate IS NULL THEN DATEADD(WEEK, -1, (GETDATE())) ELSE @StartDate END);
SELECT @@SERVERNAME AS ServerName, DatabaseName, SchemaName, ObjectName, ObjectType, IndexName, IndexType, StartTime, ErrorNumber, ExtendedInfo.value('(/ExtendedInfo/PageCount)[1]', 'varchar(15)') AS PageCount, ExtendedInfo.value('(/ExtendedInfo/Fragmentation)[1]', 'varchar(15)') AS Fragmentation, (CASE WHEN [Command] LIKE '%REBUILD%' THEN 'REBUILD' WHEN [Command] LIKE '%REORGANIZE%' THEN 'REORG' ELSE '' END) AS ActionType, ErrorMessage FROM CommandLog WHERE (StartTime >= @StartDate) AND (CONVERT(FLOAT, ExtendedInfo.value('(/ExtendedInfo/Fragmentation)[1]', 'varchar(15)')) > @FragmentationLimit) AND (ErrorNumber > (CASE WHEN @ErrorOnly = 1 THEN 0 ELSE -1 END)) ORDER BY Fragmentation DESC;
SET TRAN ISOLATION LEVEL READ COMMITTED;
END

