CREATE PROCEDURE [dbo].[DatabaseBackup]

@Databases nvarchar(max),
@Directory nvarchar(max) = NULL,
@BackupType nvarchar(max),
@Verify nvarchar(max) = 'N',
@CleanupTime int = NULL,
@Compress nvarchar(max) = NULL,
@CopyOnly nvarchar(max) = 'N',
@ChangeBackupType nvarchar(max) = 'N',
@BackupSoftware nvarchar(max) = NULL,
@CheckSum nvarchar(max) = 'N',
@BlockSize int = NULL,
@BufferCount int = NULL,
@MaxTransferSize int = NULL,
@NumberOfFiles int = NULL,
@CompressionLevel int = NULL,
@Description nvarchar(max) = NULL,
@Threads int = NULL,
@Throttle int = NULL,
@Encrypt nvarchar(max) = 'N',
@EncryptionAlgorithm nvarchar(max) = NULL,
@ServerCertificate nvarchar(max) = NULL,
@ServerAsymmetricKey nvarchar(max) = NULL,
@EncryptionKey nvarchar(max) = NULL,
@ReadWriteFileGroups nvarchar(max) = 'N',
@OverrideBackupPreference nvarchar(max) = 'N',
@LogToTable nvarchar(max) = 'N',
@Execute nvarchar(max) = 'Y'

AS

BEGIN

	SET NOCOUNT ON;

	declare @Weekday varchar(20) = DATENAME(dw,GETDATE());
	declare @FileList table (ID int IDENTITY(1,1), DatabaseName varchar(50), InstanceName varchar(50), Found bit NULL);
	declare @DirectoryList table (ID int IDENTITY(1,1), Directory varchar(250));
	declare @PhysicalFileList table (ID int IDENTITY(1,1), Directory varchar(250),FileDate date, FileMetaData varchar(500));
	declare @temptable table (RawData nvarchar(400));
	declare @curDirectory varchar(250);
	declare @curDatabaseName varchar(50);
	declare @cmdPath varchar(600)
	declare @DirectoryCount int;
	declare @FileListCount int;
	declare @iDir int=0;
	declare @iFList int=0;

	-- Get list of active files on watch
	Insert into @FileList
	(DatabaseName,InstanceName,Found)
	Select DatabaseName,InstanceName,0
	From [dbo].[FileLists]
	where isactive=1
	Order by InstanceName, DatabaseName;

	select @FileListCount = count(*) from @FileList;

	-- Create a table of directories
	Insert into @DirectoryList
	(Directory)
	SELECT DISTINCT ( @DirectoryBase+@Weekday+'\'+InstanceName+'\')
	FROM @FileList;

	select @DirectoryCount= Count(*) FROM @DirectoryList;

	-- create a table with the physical files and their dates
	while @iDir < @DirectoryCount
	begin
		set @iDir = @iDir+1;
		select		@cmdPath = 'dir '+Directory,
					@curDirectory = Directory 
		from @DirectoryList
		where ID = @iDir;

		Insert into @temptable
		EXEC xp_cmdshell @cmdPath;

		delete from @temptable 
		where ISNUMERIC(left(RawData,2))=0;

		insert into @PhysicalFileList
		(Directory,FileDate,FileMetaData)
		Select	@curDirectory,
				CONVERT(Date,left(RawData,20)) [Date],
				RTRIM(LTRIM(RIGHT(RawData,LEN(RawData)-20))) [Line]
		from @temptable;	

		select @curDirectory = '', @cmdPath=''
		delete from @temptable ;
	end

	-- update valid file list
	while @iFList < @FileListCount
	begin
		set @iFList = @iFList+1;
		select	@curDatabaseName = DatabaseName,
				@curDirectory = InstanceName
		From @FileList
		where ID = @iFList;

		Update @FileList
		set Found = 	
						(Select COUNT(*) 
						from @PhysicalFileList 
						where		(Directory like '%'+@curDirectory+'%') 
								and (FileMetaData like '%'+@curDatabaseName+'%')
								and (FileDate = convert(Date,(getdate()))))

		where ID = @iFList;

		select @curDatabaseName='', @curDirectory='';
	end

	select 'Total file count' [InstanceName], CONVERT(varchar(50),COUNT(*)) [DatabaseName]
	from @FileList
	UNION
	select InstanceName, DatabaseName
	from @FileList where found <> 1;

END

CREATE PROCEDURE [dbo].[DatabaseIntegrityCheck]

@Databases nvarchar(max),
@CheckCommands nvarchar(max) = 'CHECKDB',
@PhysicalOnly nvarchar(max) = 'N',
@NoIndex nvarchar(max) = 'N',
@ExtendedLogicalChecks nvarchar(max) = 'N',
@TabLock nvarchar(max) = 'N',
@FileGroups nvarchar(max) = NULL,
@Objects nvarchar(max) = NULL,
@LockTimeout int = NULL,
@LogToTable nvarchar(max) = 'N',
@Execute nvarchar(max) = 'Y'

AS

BEGIN
	SET NOCOUNT ON;

	DECLARE @FileSplitCount int;
	DECLARE @iCount int;
	DECLARE @upCount int=1;
	DECLARE @deletePath varchar(250);
	DECLARE @Backup VARCHAR(2000);
	DECLARE @Query VARCHAR(4000);
	DECLARE @CopyOption VARCHAR(50);
	SELECT  @CopyOption = (CASE WHEN @CopyOnly = 0 then ';' ELSE ', COPY_ONLY;' END);
	DECLARE @FileList TABLE (ID int IDENTITY(1,1), FilePath varchar(250))
	INSERT INTO  @FileList (FilePath)	
	SELECT 
	   B.physical_device_name
	FROM 
			(		SELECT   
					   CONVERT(CHAR(100), SERVERPROPERTY('Servername')) AS Server, 
					   msdb.dbo.backupset.database_name,  
					   MAX(msdb.dbo.backupset.backup_finish_date) AS last_db_backup_date 
				   FROM    msdb.dbo.backupmediafamily  
					   INNER JOIN msdb.dbo.backupset ON msdb.dbo.backupmediafamily.media_set_id = msdb.dbo.backupset.media_set_id  
				   WHERE   msdb..backupset.type = 'D' 
					AND  msdb..backupset.database_name = @DB
				   GROUP BY 
					   msdb.dbo.backupset.database_name  
			) AS A 

	   LEFT JOIN 
			(    SELECT   
				   CONVERT(CHAR(100), SERVERPROPERTY('Servername')) AS Server, 
				   msdb.dbo.backupset.database_name,  
				   msdb.dbo.backupset.backup_start_date,  
				   msdb.dbo.backupset.backup_finish_date, 
				   msdb.dbo.backupset.expiration_date, 
				   msdb.dbo.backupset.backup_size,  
				   msdb.dbo.backupmediafamily.logical_device_name,  
				   msdb.dbo.backupmediafamily.physical_device_name,   
				   msdb.dbo.backupset.name AS backupset_name, 
				   msdb.dbo.backupset.description 
				FROM   msdb.dbo.backupmediafamily  
				   INNER JOIN msdb.dbo.backupset ON msdb.dbo.backupmediafamily.media_set_id = msdb.dbo.backupset.media_set_id  
				WHERE  msdb..backupset.type = 'D' 
			) AS B 
	   ON A.[server] = B.[server] AND A.[database_name] = B.[database_name] AND A.[last_db_backup_date] = B.[backup_finish_date] 
	ORDER BY A.database_name;

	-- Delete any old backup file first. This is needed in case we cahnge the number of files, buffer or block size.
	SELECT @iCount = COUNT(*) FROM @FileList;
	WHILE @iCount > 0
	BEGIN
		SELECT @deletePath = 'del ' + FilePath FROM @FileList WHERE ID = @iCount;
		exec master..xp_cmdshell @deletePath;
		SELECT @iCount = @iCount-1;
	END;

	-- Determine how many files to split database
	SELECT @FileSplitCount=		(CASE	WHEN @DoNotSplit = 1 THEN 1
										ELSE ( CONVERT(INT,(CAST(SUM(size) * 8. / 1024 AS DECIMAL(8,2)))/18000)+1) END) 
	FROM sys.master_files WITH(NOWAIT)
	WHERE database_id = DB_ID(@DB);

	SELECT @DoNotSplit = (CASE WHEN @FileSplitCount < 2 THEN 1 ELSE @DoNotSplit END);

	-- Create backup TSQL
	SET @Backup = @PATH + REPLACE(REPLACE(@DB,'[',''),']','') + '.bak';	
	SET @Query =	'BACKUP DATABASE [' + @DB + '] TO DISK = N' 
					+ (CASE	WHEN @DoNotSplit =1 then QUOTENAME(@Backup, '''')
							ELSE QUOTENAME((REPLACE(@Backup,'.bak','_split_1.bak')), '''')
							END); 
	WHILE @FileSplitCount > 1
	BEGIN
		SET @upCount = @upCount+1;
		SET @Query = @Query + ', DISK = N' + QUOTENAME((REPLACE(@Backup,'.bak',('_split_'+CONVERT(varchar(12),@upCount)+'.bak'))), '''') 
		SET @FileSplitCount = @FileSplitCount-1;
	END;
	SET @Query = @Query
					+ ' WITH FORMAT, INIT, NAME = N' + QUOTENAME((@DB+'-Full Database Backup'), '''') 
					+ ', SKIP, NOREWIND, NOUNLOAD, COMPRESSION, STATS = 10'
					+ @CopyOption;

	-- Execute backup
	DBCC TRACEON (3605, -1);
	DBCC TRACEON (3213, -1);
	EXEC(@Query);
	DBCC TRACEOFF(3605, -1);
	DBCC TRACEOFF(3213, -1);

END

CREATE PROCEDURE [dbo].[DBBackupSplit]
@DB varchar(100), -- Database to be Backed Up
@Path varchar(500) = '', -- Path where the .BAK file will be stored
@CopyOnly BIT = 1,
@DoNotSplit BIT = 0
AS

BEGIN
	SET NOCOUNT ON;

	DECLARE @SQL VARCHAR(MAX), @Date DATETIME;

	SET @SQL = ''
	SET @Date = CONVERT(char(10), GETDATE(), 120);

	WITH DL AS (SELECT D.DatabaseID
				FROM dbo.Databases D (NOLOCK)
				INNER JOIN dbo.DatabaseCount C (NOLOCK)
					ON D.DatabaseID = C.DatabaseID
					AND C.CountDate = @Date
				)
	SELECT @SQL = @SQL + '
	EXEC dbo.DBCount_' + C.CountType + '
		@DatabaseID = ' + CAST(D.DatabaseID AS VARCHAR(20)) + ',
		@DatabaseName = ''' + D.DatabaseName + ''',
		@CountTable = ''' + D.CountTable + ''',
		@CountColumn = ''' + ISNULL(D.CountColumn,'') + ''';
	'
	FROM dbo.Databases D
	INNER JOIN dbo.CountType C
	ON D.CountTypeID = C.CountTypeID
	LEFT JOIN DL
	ON D.DatabaseID = DL.DatabaseID
	WHERE DL.DatabaseID IS NULL
	AND D.IsCounter = 1

	EXEC (@SQL)
END

BEGIN
	SET NOCOUNT ON;

	DECLARE @SQL NVARCHAR(MAX), @Params NVARCHAR(MAX), @Date DATETIME;

	SET @Date = CONVERT(char(10), GETDATE(), 120);

	SET @SQL = '
	USE ' + @DatabaseName + ';

	INSERT INTO Maintenance.dbo.DatabaseCount (DatabaseID,CountDate,DatabaseCount)
	VALUES (@DatabaseID,@Date,IDENT_CURRENT(@CountTable))
	'
	SET @Params = '
	@DatabaseID INT,
	@Date DATETIME,
	@CountTable VARCHAR(50)
	'

	EXEC sp_executesql @SQL, @Params, @DatabaseID, @Date, @CountTable
END

CREATE PROCEDURE [dbo].[DBCount_IDENTITY]
	@DatabaseID INT,
	@DatabaseName VARCHAR(50),
	@CountTable VARCHAR(50),
	@CountColumn VARCHAR(50)
AS

BEGIN
	SET NOCOUNT ON;

	DECLARE @SQL NVARCHAR(MAX), @Params NVARCHAR(MAX), @Date DATETIME;

	SET @Date = CONVERT(char(10), GETDATE(), 120);

	SET @SQL = '
	USE ' + @DatabaseName + ';

	DECLARE @Max BIGINT
	SELECT @Max = MAX(' + @CountColumn + ')
	FROM ' + @CountTable + ' (NOLOCK)

	INSERT INTO Maintenance.dbo.DatabaseCount (DatabaseID,CountDate,DatabaseCount)
	VALUES (@DatabaseID,@Date,@Max)
	'
	SET @Params  = '
	@DatabaseID INT,
	@Date DATETIME
	'

	EXEC sp_executesql @SQL, @Params, @DatabaseID, @Date
END

CREATE PROCEDURE [dbo].[DBCount_MAXID]
	@DatabaseID INT,
	@DatabaseName VARCHAR(50),
	@CountTable VARCHAR(50),
	@CountColumn VARCHAR(50)
AS

BEGIN
	SET NOCOUNT ON;

	DECLARE @SQL NVARCHAR(MAX), @Params NVARCHAR(MAX), @Date DATETIME;

	SET @Date = CONVERT(char(10), GETDATE(), 120);

	SET @SQL = '
	USE ' + @DatabaseName + ';

	DECLARE @Cnt BIGINT
	SELECT @Cnt = rowcnt
	FROM sys.sysindexes (NOLOCK)
	WHERE indid < 2
	AND id = OBJECT_ID(@CountTable)	

	INSERT INTO Maintenance.dbo.DatabaseCount (DatabaseID,CountDate,DatabaseCount)
	VALUES (@DatabaseID,@Date,@Cnt)
	'
	SET @Params  = '
	@DatabaseID INT,
	@Date DATETIME,
	@CountTable VARCHAR(50)
	'

	EXEC sp_executesql @SQL, @Params, @DatabaseID, @Date, @CountTable
END

CREATE PROCEDURE [dbo].[DBCount_ROWCOUNT]
	@DatabaseID INT,
	@DatabaseName VARCHAR(50),
	@CountTable VARCHAR(50),
	@CountColumn VARCHAR(50)
AS

BEGIN
	SET NOCOUNT ON;

	DECLARE @Query VARCHAR(4000);
	DECLARE @curFileName VARCHAR(100);
	DECLARE @FileCount INT;
	DECLARE @FileGroupCount INT;
	DECLARE @LoopCount1 INT=0;
	DECLARE @LoopCount2 INT=0;
	DECLARE @isLog BIT = 0;

	-- Get list of files for the backup. This assumes ALL backup files are in the same directory.
	declare @cmdPath varchar(280) = 'dir ' + @RestoreFromPath + ' /b'
	declare @files table (ID int IDENTITY, FileName varchar(100))
	declare @files2 table (ID int IDENTITY, FileName varchar(100))
	insert into @files execute xp_cmdshell @cmdPath;
	delete from @files
	where ([FileName] not like @DatabaseName+'_'+'%' or [FileName] <> @DatabaseName+'.bak') or ([FileName] is null);

	-- Reset ID counter for remaining entries
	insert into @files2 (FileName)
	SELECT filename
	From @files
	Order by ID;

	select  @FileCount = count(*)
	from @files2;

	-- Drop Database if it already exists in the destination.
	IF @DropExistingDatabase =1 and EXISTS(SELECT * FROM master.dbo.sysdatabases WHERE name = @DatabaseName) 
	BEGIN
		SET @Query = 'DROP DATABASE ' + @DatabaseName
		EXEC (@Query)
	END;

	-- Peek into first Backup file to get paramaters needed.
	DECLARE @Backup VARCHAR(500);
	SELECT @Backup = @RestoreFromPath + FileName
	From @files2
	Where ID = 1;

	SET @Query = 'RESTORE FILELISTONLY FROM DISK = ' + QUOTENAME(@Backup , '''')

	DECLARE @Temp as TABLE
	(ID int IDENTITY(1,1),
	LogicalName VARCHAR(100),
	PhysicalName VARCHAR(100),
	Type VARCHAR(1),
	FileGroupName VARCHAR(50) ,
	Size BIT ,
	MaxSize BIGINT,
	FileID INT,
	CreateLSN VARCHAR(100),
	DropLSN VARCHAR(100),
	UniqueID UNIQUEIDENTIFIER,
	ReadOnlyLSN BIT,
	ReadWriteLSN BIT,
	BackupSizeInBytes BIGINT,
	SourceBlockSize INT,
	FileGroupID INT,
	LogGroupGUID UNIQUEIDENTIFIER,
	DifferentialBaseLSN VARCHAR(100),
	DifferentialBaseGUID UNIQUEIDENTIFIER,
	IsReadOnly BIT,
	IsPresent BIT,
	TDEThumbprint VARCHAR(100)
	,SnapshotURL VARCHAR(100)		-- UNCOMMENT for SQL 2016
	)

	INSERT @Temp EXEC (@Query)
	select * from @Temp;

	select @FileGroupCount = COUNT(*) FROM @Temp;

	-- Complie the RESTORE query with multiple files and multiple filegroups as required.
	SET @Query = 'RESTORE DATABASE [' + @DatabaseName + '] FROM '

	WHILE @LoopCount1 < @FileCount
	BEGIN
		set  @LoopCount1 = @LoopCount1 + 1;
		select @curFileName = [FileName]
		FROM @files2 
		WHERE ID = @LoopCount1;
		SET @Query = @Query + 'DISK = ' + QUOTENAME((@RestoreFromPath+@curFileName), '''')
		SET @curFileName = ''
		SET @Query = @Query + (SELECT CASE WHEN @LoopCount1 < @FileCount THEN ', ' ELSE ' ' END);
	END;
	SET @Query = @Query + ' WITH ';

	WHILE @LoopCount2 < @FileGroupCount
	BEGIN
		set		@LoopCount2 = @LoopCount2 + 1;
		select	@curFileName = LogicalName,
				@isLog  = (Case when Type='L' Then 1 else 0 end)
		from @temp
		where ID = @LoopCount2;
		SELECT @Query = @Query	+ 'MOVE ' + QUOTENAME(@curFileName, '''') + ' TO '
								+ (CASE WHEN @isLog=1 THEN QUOTENAME((@RestoreToPathLog+@curFileName+'.ldf'), '''')
										ELSE QUOTENAME((@RestoreToPathData+@curFileName+'.mdf'), '''')
										END)
								+ ', ';
		SET @curFileName = '';
	END;
	SET @Query = @Query + ' REPLACE, NOUNLOAD, STATS = 10;';
	-- Executequery
	EXEC (@Query);
END

CREATE PROCEDURE [dbo].[DBRestoreSplit]
	@DatabaseName VARCHAR(100), 
	@RestoreFromPath varchar(250),		-- 'D:\MSSQL\Backup\'  Backup Location. 
	@RestoreToPathData varchar(250),	-- 'D:\MSSQL\Data\'    Path where the database will be restored
	@RestoreToPathLog varchar(250),		-- 'D:\MSSQL\Log\'
	@DropExistingDatabase bit = 0		-- Set to 1 if multiple files is expected
AS

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
SELECT index_advantage, unique_compiles, user_seeks, last_user_seek, mid.statement, mid.equality_columns, mid.inequality_columns, mid.included_columns FROM (SELECT user_seeks * avg_total_user_cost * (avg_user_impact * 0.01) AS index_advantage, migs.unique_compiles, migs.user_seeks, migs.last_user_seek, migs.group_handle FROM sys.dm_db_missing_index_group_stats migs) AS migs_adv INNER JOIN sys.dm_db_missing_index_groups AS mig ON migs_adv.group_handle = mig.index_group_handle INNER JOIN sys.dm_db_missing_index_details AS mid ON mig.index_handle = mid.index_handle WHERE migs_adv.index_advantage > 1000 ORDER BY migs_adv.index_advantage DESC;
END

CREATE PROCEDURE [dbo].[IndexOptimize]

@Databases nvarchar(max),
@FragmentationLow nvarchar(max) = NULL,
@FragmentationMedium nvarchar(max) = 'INDEX_REORGANIZE,INDEX_REBUILD_ONLINE,INDEX_REBUILD_OFFLINE',
@FragmentationHigh nvarchar(max) = 'INDEX_REBUILD_ONLINE,INDEX_REBUILD_OFFLINE',
@FragmentationLevel1 int = 5,
@FragmentationLevel2 int = 30,
@PageCountLevel int = 200,
@SortInTempdb nvarchar(max) = 'N',
@MaxDOP int = NULL,
@FillFactor int = 95,
@PadIndex nvarchar(max) = NULL,
@LOBCompaction nvarchar(max) = 'Y',
@UpdateStatistics nvarchar(max) = NULL,
@OnlyModifiedStatistics nvarchar(max) = 'N',
@StatisticsSample int = NULL,
@StatisticsResample nvarchar(max) = 'N',
@PartitionLevel nvarchar(max) = 'Y',
@MSShippedObjects nvarchar(max) = 'N',
@Indexes nvarchar(max) = NULL,
@TimeLimit int = NULL,
@Delay int = NULL,
@WaitAtLowPriorityMaxDuration int = NULL,
@WaitAtLowPriorityAbortAfterWait nvarchar(max) = NULL,
@LockTimeout int = NULL,
@LogToTable nvarchar(max) = 'N',
@Execute nvarchar(max) = 'Y'

AS

BEGIN SET NOCOUNT ON;
DECLARE @Date INT SET @Date = CAST(REPLACE(CONVERT(VARCHAR(10),DATEADD(DAY,-1,GETDATE()),120),'-','') AS INT) DECLARE @Failed INT ; WITH T AS (SELECT @Date AS JobDate, COUNT(1) AS Counter, SUM(CASE WHEN run_status = 0 THEN 1 ELSE 0 END) AS Failed, SUM(CASE WHEN run_status = 1 THEN 1 ELSE 0 END) AS Succeeded, SUM(CASE WHEN run_status = 2 THEN 1 ELSE 0 END) AS Retry, SUM(CASE WHEN run_status = 3 THEN 1 ELSE 0 END) AS Canceled FROM msdb..sysjobhistory H (NOLOCK) LEFT JOIN dbo.JobCounter C (NOLOCK) ON C.JobDate = @Date WHERE step_id = 0 AND run_date = @Date ) INSERT INTO JobCounter (JobDate, JobCount, JobFailed, JobSucceeded, JobRetry, JobCanceled) SELECT T.JobDate, T.Counter, T.Failed, T.Succeeded, T.Retry, T.Canceled FROM T LEFT JOIN dbo.JobCounter C (NOLOCK) ON C.JobDate = T.JobDate WHERE C.JobDate IS NULL
SELECT @Failed = COUNT(1) FROM dbo.JobHistory WHERE JobDate = @Date ; WITH JL AS (SELECT job_id FROM msdb..sysjobhistory (NOLOCK) WHERE step_id = 0 AND run_status = 0 ) INSERT INTO dbo.JobHistory (JobName,JobDate,JobTime,JobFailed) SELECT J.name AS JobName, H.run_date AS JobDate, H.run_time AS JobTime, CASE WHEN H.run_status = 0 THEN 1 ELSE 0 END FROM msdb..sysjobs J (NOLOCK) INNER JOIN JL ON J.job_id = jl.job_id INNER JOIN msdb..sysjobhistory H ON J.job_id = H.job_id AND H.step_id = 0 AND @Failed = 0 ORDER BY 2 DESC, 1, 3 DESC END

BEGIN

	SET NOCOUNT ON;

    DECLARE @SQL VARCHAR(MAX);
	DECLARE @tSubject VARCHAR(50);
	DECLARE @tBody VARCHAR(500);
	DECLARE @DataLogicalName VARCHAR(100);
	DECLARE @LogLogicalName VARCHAR(100);

	SELECT @DataLogicalName = [name]
	FROM sys.master_files
	WHERE DB_NAME([database_id]) = @DatabaseName and [name] NOT LIKE '%log%';

	SELECT @LogLogicalName = [name]
	FROM sys.master_files
	WHERE DB_NAME([database_id]) = @DatabaseName and [name] LIKE '%log%';

    SET @SQL = 'RESTORE DATABASE [' + @DatabaseName + '] FROM  DISK = N''C:\MSSQL\Restore\' + @DatabaseName + '.bak'' WITH  FILE = 1,  MOVE N''' 
				+ @DataLogicalName
				-- + @DatabaseName + (CASE WHEN @DatabaseName = 'MGIS' THEN '_Data' ELSE '' END) 
				+ ''' TO N''C:\MSSQL\Data\' + @DatabaseName + '.mdf'',  MOVE N''' 
				+ @LogLogicalName
				-- + @DatabaseName +  (CASE WHEN @DatabaseName ='CBGEU' THEN '' WHEN @DatabaseName = 'CBGUS' THEN '' ELSE '_' END) + 'log'
				+ ''' TO N''C:\MSSQL\Data\' + @DatabaseName + '_log.ldf'',  NOUNLOAD,  REPLACE,  STATS = 10'
	begin try
		EXEC (@SQL)
	end try
	begin catch
		begin try
			SELECT	@tSubject	= 'IAD-REPORT-01.'+@DatabaseName+' Restore Failed',
					@tBody		= 'IAD-REPORT-01.'+@DatabaseName+' nightly restore job failed.';
			EXEC msdb.dbo.sp_send_dbmail
			@profile_name	= 'DEVOPS',
			@recipients		= 'devops@coventbridge.com',
			@subject		= @tSubject,
			@body			= @tBody;
		end try
		begin catch
			Print 'email failed'
		end catch
	end catch

END

BEGIN
	DECLARE @Script AS NVARCHAR(MAX);
	DECLARE @sql NVARCHAR(200);
	DECLARE @Results table (ScriptBlock NVARCHAR(MAX))

	SET @sql = 'USE'
	SELECT @sql = @sql + ' ' + @DATABSE + ';'
	EXEC (@sql);

	SELECT @Script = '  DECLARE @COLUMNNAME AS NVARCHAR(MAX) ; 	
	SELECT @COLUMNNAME = STUFF(( 
    SELECT ' + CHAR(39) + ',' + CHAR(39) + ' +QUOTENAME(C.column_name) 
    FROM information_schema.columns AS C 
    WHERE C.table_name = ' + CHAR(39) + @TABLENNAME + CHAR(39) + '
    FOR XML path(' + CHAR(39) + '' + CHAR(39) + ')
    ), 1, 1,' + CHAR(39) + '' + CHAR(39) + ')  ' + CHAR(13) + ' PRINT' + CHAR(39) + 'SELECT ' + CHAR(39) + '+@COLUMNNAME + CHAR(13) +' + CHAR(39) + 'FROM ' + @TABLENNAME + CHAR(39) + ' +CHAR(13) +' + CHAR(13) + CHAR(39) + 'GO' + CHAR(39);


	INSERT INTO @Results (ScriptBlock)
	EXEC (@Script);

	SELECT ScriptBlock FROM @Results;

END

DECLARE @hr INT,         --the HRESULT returned from 
       @objFileSystem INT,              --the FileSystem object
       @objFile INT,            --the File object
       @ErrorObject INT,        --the error object
       @ErrorMessage VARCHAR(255),--the potential error message
       @Path VARCHAR(100),--
       @ShortPath VARCHAR(100),
       @Type VARCHAR(100),
       @DateCreated datetime,
       @DateLastAccessed datetime,
       @DateLastModified datetime



SET nocount ON

SELECT @hr=0,@errorMessage='opening the file system object '
EXEC @hr = sp_OACreate 'Scripting.FileSystemObject',
                                       @objFileSystem OUT
IF @hr=0 SELECT @errorMessage='accessing the file '''
                                       +@Filename+'''',
       @ErrorObject=@objFileSystem
IF @hr=0 EXEC @hr = sp_OAMethod @objFileSystem,
         'GetFile',  @objFile out,@Filename
IF @hr=0 
       SELECT @errorMessage='getting the attributes of '''
                                       +@Filename+'''',
       @ErrorObject=@objFile
IF @hr=0 EXEC @hr = sp_OAGetProperty 
             @objFile, 'Path', @path OUT
IF @hr=0 EXEC @hr = sp_OAGetProperty 
             @objFile, 'ShortPath', @ShortPath OUT
IF @hr=0 EXEC @hr = sp_OAGetProperty 
             @objFile, 'Type', @Type OUT
IF @hr=0 EXEC @hr = sp_OAGetProperty 
             @objFile, 'DateCreated', @DateCreated OUT
IF @hr=0 EXEC @hr = sp_OAGetProperty 
             @objFile, 'DateLastAccessed', @DateLastAccessed OUT
IF @hr=0 EXEC @hr = sp_OAGetProperty 
             @objFile, 'DateLastModified', @DateLastModified OUT


IF @hr<>0
       BEGIN
       DECLARE 
               @Source VARCHAR(255),
               @Description VARCHAR(255),
               @Helpfile VARCHAR(255),
               @HelpID INT

       EXECUTE sp_OAGetErrorInfo  @errorObject, 
               @source output,@Description output,
                               @Helpfile output,@HelpID output

       SELECT @ErrorMessage='Error whilst '
                               +@Errormessage+', '
                               +@Description
       RAISERROR (@ErrorMessage,16,1)
       END
EXEC sp_OADestroy @objFileSystem
EXEC sp_OADestroy @objFile
SELECT [Path]=  @Path,
       [ShortPath]=    @ShortPath,
       [Type]= @Type,
       [DateCreated]=  @DateCreated ,
       [DateLastAccessed]=     @DateLastAccessed,
       [DateLastModified]=     @DateLastModified
RETURN @hr

BEGIN

	DECLARE @NewUserID UNIQUEIDENTIFIER;
	SELECT @NewUserID=UserID FROM ReportServer.dbo.Users WHERE UserName = 'ORLANDO\S-SSRSReports';

	UPDATE ReportServer.dbo.Subscriptions 
	SET OwnerID = @NewUserID 
	WHERE OwnerID <> @NewUserID;

END
